        this.setXConstraint(minw, 1000);
        this.setYConstraint(0, 0);
        this.minX = x - minw;
        this.maxX = x + 1000;
        this.startPos = x;
        if (!this.view.mainWrap) { // this is Bootstrap code..
            this.getDragEl().style.display='block';
        }
        
