     /**
     * Validates the field value
     * @return {Boolean} True if the value is valid, else false
     */
    validate : function(){
        //if(this.disabled || this.validateValue(this.processValue(this.getRawValue()))){
        if(this.disabled || this.validateValue(this.getRawValue())){
            // check for warning
            if(this.shouldWarn(this.getRawValue())) {
                this.markWarning();
            }
            else {
                this.markValid();
            }
            return true;
        }
        
        this.markInvalid();
        return false;
    },
    
    
    /**
     * Validates a value according to the field's validation rules and marks the field as invalid
     * if the validation fails
     * @param {Mixed} value The value to validate
     * @return {Boolean} True if the value is valid, else false
     */
    validateValue : function(value)
    {
        if(this.getVisibilityEl().hasClass('hidden')){
            return true;
        }
        
        if(value.length < 1)  { // if it's blank
            if(this.allowBlank){
                return true;
            }
            return false;
        }
        
        if(value.length < this.minLength){
            return false;
        }
        if(value.length > this.maxLength){
            this.invalidText = this.maxLengthText;
            return false;
        }
        if(this.vtype){
            var vt = Roo.form.VTypes;
            if(!vt[this.vtype](value, this)){
                return false;
            }
        }
        if(typeof this.validator == "function"){
            var msg = this.validator(value);
            if (typeof(msg) == 'string') {
                this.invalidText = msg;
            }
            if(msg !== true){
                return false;
            }
        }
        
        if(this.regex && !this.regex.test(value)){
            return false;
        }
        
        return true;
    },

    shouldWarn : function(value)
    {
        if(value.length > this.maxLengthWarn) {
            this.warningText = this.maxLengthWarnText;
            return true;
        }

        if(typeof this.warningIndicator == "function"){
            var msg = this.warningIndicator(value);
            if (typeof(msg) == 'string') {
                this.warningText = msg;
            }
            if(msg !== false){
                return true;
            }
        }
        return false;
    },
    
